sysauto_script.script:
closeSRs();
closeTasks();

function closeSRs() {
  gs.debug('Close Service Requests');
  var gr = new GlideRecord('x_76760_iot_sm_request');
  gr.addQuery('active', '=', true);
  gr.query();

  while(gr.next()) {
    closeSR(gr.sys_id);
  }
}

function closeSR(sys_id) {
  try {
    var gr = new GlideRecord('x_76760_iot_sm_request');
    if(gr.get(sys_id)) {
      gs.debug('Cancelling ' + gr.number);
      gr.setValue('state', 7);
      gr.work_notes = 'cancelled by batch job';
      gr.update();
    }
  } catch(e) {}

  return;
}

function closeTasks() {
  gs.debug('Close Tasks');
  var gr = new GlideRecord('x_76760_iot_sm_task');
  gr.addQuery('active', '=', true);
  gr.query();

  while(gr.next()) {
    closeTask(gr.sys_id);
  }
}

function closeTask(sys_id) {
  try {
    var gr = new GlideRecord('x_76760_iot_sm_task');
    if(gr.get(sys_id)) {
      gs.debug('Cancelling ' + gr.number);
      gr.setValue('state', 7);
      gr.work_notes = 'cancelled by batch job';
      gr.update();
    }
  } catch(e) {}

  return;
}
